      type:
        | "ArrayPattern"
        | "AssignmentExpression"
        | "CatchClause"
        | "ForOfStatement"
        | "FormalParameters"
        | "ForInStatement"
        | "ForStatement"
        | "Identfier"
        | "ObjectPattern"
        | "RestElement"
        | "VariableDeclarator",
    };

